obtenerCorreoHTML <- function(titulo, asunto, mensaje){
  logo <- 'https://registromorant.com.mx/utility/logo-morena.png'
  html <- glue::glue("<div style='background-color: #B5261E;width:100%;height: 10px;box-shadow: 1px 2px 2px 0px rgba(0,0,0,.3);position: relative;'></div><div style='background-image:url({logo});background-size: 50%;background-repeat:no-repeat;background-position:center;width:100%;height:250px;display:block;margin:auto;background-color: #fafafb;'></div><div style='color:#333;background-color: #fafafb;display:block;padding: 5px 20px;margin-top:0px;font-family:sans-serif;border: 1px solid #eaeaea;'><h3 style='color: #B5261E !important; text-align: left;font-size: 20px;padding:10px 0px 3px 0px;margin-bottom:0;text-transform: uppercase;margin-top: 10px;'>{titulo}</h3><p style='font-size: 15px;text-align: left;background-color:#fafafb;color: #333;padding: 0px 0px;padding-bottom: 18px;border-radius:8px;width: 100%;display:block;margin: 0 0;'>Asunto: {asunto}</p><p style='text-align: left;background-color:#fafafb;color: #333;padding: 0px 0px;padding-bottom: 18px;border-radius:8px;width: 100%;display:block;margin: 0 0;'>{mensaje}</p></div>");
  return (html);
}